$_MODULE['<{lgcanonicalurls}prestashop>info_message_82a6a36aec7f2f7cf893f3f3e0fc995a'] = 'Onze module vertrouwt op deze functionaliteit om standaard te werken op Prestashop 1.7.x, waardoor wijzigingen op de canonieke intern.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_50fa9ef2c74859474e93a18776b41bf2'] = 'Met behulp van deze optie, zal onze module de standar haak displayHeader intead van Prestashop Core functionallity gebruiken om de tag te tonen.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_6cae151e25c8b314ca3c3e9562f6915a'] = 'Uw thema moet compatibel zijn met deze functie om de tag te krijgen.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_19c5d1faf93fc0c2af4d3e06a5da31b2'] = 'Als u de tag niet ziet, is dat meestal omdat het thema deze nieuwe functie niet ondersteunt.';
