namespace Scaledev\MiraklPhpConnector\Model\Shop;

use Scaledev\MiraklPhpConnector\Core\Model\AbstractModel;

/**
 * Class KYC
 *
 * @package Scaledev\MiraklPhpConnector
 * @author Louis Pavoine <contact@scaledev.fr>
 */
final class ShopKyc extends AbstractModel
{
    /**
     * KYC refusal reason
     * Api data: reason
     *
     * @var string
     */
    private $reason;

    /**
     * KYC status
     * Api data: status
     *
     * @require true
     * @value : PENDING_SUBMISSION, PENDING_APPROVAL, REFUSED, APPROVED
     * @var string
     */
    private $status;

    /**
     * @return string
     */
    public function getReason()
    {
        return $this->reason;
    }

    /**
     * @param string $reason
     * @return $this
     */
    public function setReason($reason)
    {
        $this->reason = $reason;
        return $this;
    }

    /**
     * @return string
     */
    public function getStatus()
    {
        return $this->status;
    }

    /**
     * @param string $status
     * @return $this
     */
    public function setStatus($status)
    {
        $this->status = $status;
        return $this;
    }
}
