    foreach ($sql as $query) {
        if (!Db::getInstance()->execute($query)) {
            $install_error = 'ERROR 24010: Ha fallado la instalación: ' . $query;
            error_log("INSTALL_ERROR_1: ".$install_error);
            CorreosOficialUtils::writeInstallErrorLog( $install_error);
            return false;
        }
    }
    
    // Para MULTITIENDA traspasamos el active de correos_oficial_products (tableProducts) a correos_oficial_products_shop
    $productsActive = Db::getInstance()->executeS("SELECT * FROM " . _DB_PREFIX_ . "correos_oficial_products;");
    foreach ($shops as $id_shop) {

        /* La tienda por defecto se hace en los insert superiores, la saltamos */
        if ($id_default_shop == $id_shop) {
            continue;
        }

        foreach ($productsActive as $prod) {
            $query = "INSERT INTO " . _DB_PREFIX_ . "correos_oficial_products_shop (id_product, active, id_shop) VALUES (".$prod['id'].", 0 , ".$id_shop.");";
            $result = Db::getInstance()->execute($query);
            
            if (!$result) {
                // Si ha ido mal lo registramos
                $error = Db::getInstance()->getMsgError(); // Obtener mensaje de error
                $install_error = "Error en la inserción para el producto ID: " . $prod['id'] . " - " . $error;
                error_log($install_error);
                CorreosOficialUtils::writeInstallErrorLog($install_error);
            }
        }
    }
    
    CorreosOficialIndexes::checkIfIndexesExists();

} catch (Exception $e) {
    $error = $e->getMessage();

    // Comprobación de intento de insertar estados de Correos/CEX ya existentes en la instalación de Prestashop, si se ha desintalado el módulo y vuelto a instalar
    if (strstr($error, '1062') && strstr($query, 'order_state_lang')) {
        CorreosOficialUtils::writeInstallErrorLog('AVISO 24020: Los estados de pedido ya existen de instalaciónes del módulo anteriores. CORRECTO');
    } else {
        $install_error = $error.' ERROR 24030. Ha fallado la instalación: ' .$query;
        error_log($install_error);
        CorreosOficialUtils::writeInstallErrorLog($install_error);
    }
