$_MODULE['<{performancepro}prestashop>cacheform_ab0cf104f39708eabd07b8cb67e149ba'] = 'Cache';
$_MODULE['<{performancepro}prestashop>cacheform_a23ad1835c0121509f5d0b5732ed6f52'] = 'Das Modul stellt Seiten der Website als statischen Inhalt bereit. Dadurch werden Besucher und Crawler Ihre Website viel schneller laden. Sobald ein Produkt zum Warenkorb hinzugefügt wurde oder der Benutzer sich auf der Website einloggt, wird der der Inhalt dynamisch und PrestaShop\'s Standardverhalten wird anstatt eines statischen Seiten-Caches genutzt. Dies wird getan, um Konflikte mit anderen Modulen zu verhindern und um  Überschreibungen zu vermeiden. Statische Resourcen, wie CSS und JS werden jedoch weiterhin aus dem Browser-Cache geladen.';
$_MODULE['<{performancepro}prestashop>cacheform_6be7a69da64013e2bb0f83fb5d534f9a'] = 'Seiten-Cache';
$_MODULE['<{performancepro}prestashop>cacheform_2022e81dbc3ab1289f4a073f33813b54'] = 'Das Modul dient als statisches HTML Dokument der Website. Dadurch werden neue Besucher und Crawler ihre Webseiten viel schneller laden.';
$_MODULE['<{performancepro}prestashop>cacheform_431002c98f1579c0002a19894793742b'] = '%s Information: Wenn ihr Webshop im Debug-Modus ist, wird der Seiten-Cache ausgeschaltet.';
